import NewChart from './NewChart.js';
import fetchData from './App.js';
import React from 'react';
import {useState, useEffect, useRef} from 'react';

export default function ChartMaker(){

    const [label, setLabel] = useState();
    const [data, setData] = useState();

    useEffect(() => {
        const fetchHistory = async () => {
            const url = `http://localhost:3002/api/getData/NowData/usd/dkk`;
            console.log(url);
            const response = await fetch(url);
            if (!response.ok) {
              throw new Error('Network response was not ok');
            }
            var oldData = await response.json();
            console.log(oldData);
            var hist = await oldData.history;
            hist = await JSON.parse(hist);
            console.log("FINAL DATA : " + hist);
            if(Array.isArray(hist)){
              console.log("YAY!");
            }
            else{
              console.log("NO :(");
            }
            var labels = [];
            var newData = [];
            for(var i = 0; i < hist.length; i++){
                labels.push(i);
                newData.push(hist[i].price);
            }
            await setLabel(labels);
            await setData(newData);
            console.log("test:" + label + " " + newData);
          };
      
          fetchHistory();

    }, [label, data]);

    const [chartData, setChartData] = useState({
        labels: label,
        datasets: [
          {
            label: 'My First dataset',
            backgroundColor: 'rgba(255,99,132,0.2)',
            borderColor: 'rgba(255,99,132,1)',
            borderWidth: 1,
            hoverBackgroundColor: 'rgba(255,99,132,0.4)',
            hoverBorderColor: 'rgba(255,99,132,1)',
            data: data,
          },
        ],
      });
    
      const [chartOptions, setChartOptions] = useState({
        responsive: false,
        maintainAspectRatio: false,
      });
    /*
    const [count, setCount] = useState(0);

    useEffect(() => {
        async function getData(data, cur1, cur2){

            var url = 'http://localhost:3002/api/getData/' + data + '/' + cur1 + '/' + cur2;
            console.log(url);
            const entryData = fetch(url);
            const entries = entryData.length;
        
            var arr = [];
            for(var i = 0; i < entries; i++){
                arr.push(i);
            }
        
            var priceData = [];
            for(var i = 0; i < entryData.length; i++){
                priceData.push(entryData.catch(i).price);
            }
            const dataChart = document.getElementById("testGraph");
            let lineChart = new Chart(dataChart, {
                type : 'line',
                labels : {
                    arr,
                    data: priceData
                },
            });
        }

        getData("Day", "USD", "DKK");
    }, [count]);
    */
    return (
        <NewChart data={chartData} options={chartOptions} />
    );
}
